(function(){function resizeImageMap(img){if(typeof img.useMap==="string"&&img.useMap!==""){var map=document.querySelector('map[name="'+img.useMap.split("#")[1]+'"]');if(map){var originalWidth=img.getAttribute("orgWidth");var originalHeight=img.getAttribute("orgHeight");if(originalWidth&&originalHeight){var areas=map.getElementsByTagName("area");for(var i=0;i<areas.length;++i){var originalCoords=areas[i].getAttribute("coords");if(originalCoords){var coords=originalCoords.split(",").map(function(coord){return Math.round(parseFloat(coord)*(coord.indexOf("%")!==-1?.01:1))});areas[i].coords=coords.map(function(coord,index){return coord*(index%2===0?img.offsetWidth/originalWidth:img.offsetHeight/originalHeight)}).join(",")}}}}}}window.addEventListener("load",function(){var imgs=document.getElementsByTagName("img");for(var i=0;i<imgs.length;++i){resizeImageMap(imgs[i])}window.addEventListener("resize",function(){for(var i=0;i<imgs.length;++i){resizeImageMap(imgs[i])}})})})();